starterCode:
#include <iostream>
using namespace std;
int countSetBits(int n) { return 0; }
int main() { cout<<countSetBits(13); return 0; }

solution:
#include <iostream>
using namespace std;
int countSetBits(int n) { int c = 0; while(n) { c += n & 1; n >>= 1; } return c; }
int main() { cout<<countSetBits(13); return 0; }

starterCode:
#include <iostream>
using namespace std;
bool isPowerOf2(int n) { return false; }
int main() { cout<<isPowerOf2(16); return 0; }

solution:
#include <iostream>
using namespace std;
bool isPowerOf2(int n) { return n > 0 && (n & (n-1)) == 0; }
int main() { cout<<isPowerOf2(16); return 0; }

