starterCode:
#include <iostream>
using namespace std;
void swap(int &a, int &b) {}
int main() { int a=5, b=10; swap(a,b); cout<<a<<" "<<b; return 0; }

solution:
#include <iostream>
using namespace std;
void swap(int &a, int &b) { a = a ^ b; b = a ^ b; a = a ^ b; }
int main() { int a=5, b=10; swap(a,b); cout<<a<<" "<<b; return 0; }

starterCode:
#include <iostream>
using namespace std;
int singleNumber(int a[], int n) { return 0; }
int main() { int a[]={2,1,4,5,2,4,1}; cout<<singleNumber(a,7); return 0; }

solution:
#include <iostream>
using namespace std;
int singleNumber(int a[], int n) { int r = 0; for(int i = 0; i < n; i++) r ^= a[i]; return r; }
int main() { int a[]={2,1,4,5,2,4,1}; cout<<singleNumber(a,7); return 0; }

starterCode:
#include <iostream>
using namespace std;
int rightmostSetBit(int n) { return 0; }
int main() { cout<<rightmostSetBit(12); return 0; }

solution:
#include <iostream>
using namespace std;
int rightmostSetBit(int n) { int pos = 0; while((n & 1) == 0) { n >>= 1; pos++; } return pos; }
int main() { cout<<rightmostSetBit(12); return 0; }

