solution:
#include <iostream>
using namespace std;
int bitwiseComplement(int n) { if(n==0) return 1; int mask=1; while(mask<n) mask=(mask<<1)|1; return n^mask; }
int main() { cout<<bitwiseComplement(5); return 0; }
