#include <iostream>
using namespace std;
int numberOfSteps(int num) {
    int count = 0;
    while(num > 0) {
        if(num % 2 == 0) {
            num /= 2;  // If even, divide by 2
        } else {
            num--;     // If odd, subtract 1
        }
        count++;
    }
    return count;
}
