int getValue(char c) {
    if(c == 'M') return 1000;
    if(c == 'D') return 500;
    if(c == 'C') return 100;
    if(c == 'L') return 50;
    if(c == 'X') return 10;
    if(c == 'V') return 5;
    if(c == 'I') return 1;
    return 0;
}
int romanToInt(char s[]) {
    int result = 0;
    for(int i = 0; s[i]; i++) {
        int curr = getValue(s[i]);
        int next = getValue(s[i+1]);
        if(curr < next) result -= curr;
        else result += curr;
    }
    return result;
}
