using namespace std;
void shiftGrid(int grid[][3], int m, int n, int k) {
    int size = m * n;
    k %= size;
    int temp[size];
    for(int i = 0; i < m; i++)
        for(int j = 0; j < n; j++)
            temp[i * n + j] = grid[i][j];
    for(int i = 0; i < m; i++)
        for(int j = 0; j < n; j++) {
            int oldIndex = (i * n + j - k + size) % size;
            grid[i][j] = temp[oldIndex];
        }
