#include <iostream>
using namespace std;
int countBits(int n) {
    int count = 0;
    while(n) { count += n & 1; n >>= 1; }
    return count;
}
void sortByBits(int arr[], int n) {
    for(int i = 0; i < n - 1; i++) {
        for(int j = 0; j < n - i - 1; j++) {
            int bits1 = countBits(arr[j]);
            int bits2 = countBits(arr[j + 1]);
            if(bits1 > bits2 || (bits1 == bits2 && arr[j] > arr[j + 1])) {
                int temp = arr[j];
                arr[j] = arr[j + 1];
                arr[j + 1] = temp;
            }
        }
    }
}
int main() { int a[] = {0, 1, 2, 3, 4, 5, 6, 7, 8}; sortByBits(a, 9); for(int i = 0; i < 9; i++) cout << a[i] << " "; return 0; }
