solution:
#include <iostream>
using namespace std;
int binarySearch(int arr[], int n, int x) { int l=0, r=n-1; while(l<=r) { int m=(l+r)/2; if(arr[m]==x) return m; if(arr[m]<x) l=m+1; else r=m-1; } return -1; }
int main() { int a[] = {1,3,5,7,9}; cout << binarySearch(a, 5, 5); return 0; }

