using namespace std;
int secondLargest(int a[], int n) { int f=-1, s=-1; for(int i=0;i<n;i++) { if(a[i]>f) { s=f; f=a[i]; } else if(a[i]>s && a[i]!=f) s=a[i]; } return s; }
