solution:
#include <iostream>
using namespace std;
int findPeak(int a[], int n) { for(int i=0;i<n;i++) { bool left = i==0 || a[i]>=a[i-1]; bool right = i==n-1 || a[i]>=a[i+1]; if(left && right) return a[i]; } return -1; }
int main() { int a[]={1,3,20,4,1,0}; cout<<findPeak(a,6); return 0; }

