solution:
#include <iostream>
using namespace std;
void toggleCase(char s[]) { for(int i=0;s[i];i++) { if(s[i]>='A'&&s[i]<='Z') s[i]+=32; else if(s[i]>='a'&&s[i]<='z') s[i]-=32; } }
int main() { char s[]="Hello World"; toggleCase(s); cout<<s; return 0; }

