#include <iostream>
using namespace std;
int countPrimes(int n) { return 0; }
int main() { cout<<countPrimes(10); return 0; }

solution:
#include <iostream>
using namespace std;
int countPrimes(int n) { if(n<=2) return 0; bool np[n]={}; int cnt=0; for(int i=2;i<n;i++) { if(!np[i]) { cnt++; for(int j=i*2;j<n;j+=i) np[j]=true; } } return cnt; }
int main() { cout<<countPrimes(10); return 0; }

