solution:
#include <iostream>
using namespace std;
int mySqrt(int x) { if(x==0) return 0; long l=1,r=x; while(l<=r) { long m=(l+r)/2; if(m*m==x) return m; if(m*m<x) l=m+1; else r=m-1; } return r; }
int main() { cout<<mySqrt(8); return 0; }
