int main() { hollowDiamond(3); return 0; }

solution:
#include <iostream>
using namespace std;
void hollowDiamond(int n) { for(int i=1;i<=n;i++) { for(int j=0;j<n-i;j++) cout<<" "; cout<<"*"; if(i>1) { for(int j=0;j<2*i-3;j++) cout<<" "; cout<<"*"; } cout<<"\n"; } for(int i=n-1;i>=1;i--) { for(int j=0;j<n-i;j++) cout<<" "; cout<<"*"; if(i>1) { for(int j=0;j<2*i-3;j++) cout<<" "; cout<<"*"; } if(i>1) cout<<"\n"; } }
int main() { hollowDiamond(3); return 0; }

