starterCode:
#include <iostream>
using namespace std;
void reverse(int a[], int n) {}
int main() { int a[]={1,2,3,4,5}; reverse(a,5); for(int i=0;i<5;i++) cout<<a[i]<<" "; return 0; }

solution:
#include <iostream>
using namespace std;
void reverse(int a[], int n) {
    int left = 0, right = n - 1;
    while(left < right) {
        int temp = a[left];
        a[left] = a[right];
        a[right] = temp;
        left++;
        right--;
    }
}
int main() { int a[]={1,2,3,4,5}; reverse(a,5); for(int i=0;i<5;i++) cout<<a[i]<<" "; return 0; }

