solution:
#include <iostream>
using namespace std;
int digitSum(int n) {
    int sum = 0;
    while(n > 0) {
        sum += n % 10;  // Get last digit
        n /= 10;        // Remove last digit
    }
    return sum;
}
int main() { cout<<digitSum(1234); return 0; }

