int main() { cout<<sumAP(1,2,5); return 0; }

solution:
#include <iostream>
using namespace std;
int sumAP(int a, int d, int n) {
    // Sum = n/2 * (2a + (n-1)d)
    return n * (2*a + (n-1)*d) / 2;
}
int main() { cout<<sumAP(1,2,5); return 0; }

