#include <iostream>
using namespace std;
int sumGP(int a, int r, int n) { return 0; }
int main() { cout<<sumGP(1,2,5); return 0; }

solution:
#include <iostream>
using namespace std;
int sumGP(int a, int r, int n) {
    // Sum = a * (r^n - 1) / (r - 1)
    int rn = 1;
    for(int i = 0; i < n; i++) rn *= r;
    return a * (rn - 1) / (r - 1);
}
int main() { cout<<sumGP(1,2,5); return 0; }
