solution:
#include <iostream>
using namespace std;
bool isArmstrong(int n) {
    int sum = 0, temp = n;
    int digits = 0, t = n;
    while(t > 0) { digits++; t /= 10; }
    while(temp > 0) {
        int d = temp % 10;
        int p = 1;
        for(int i=0;i<digits;i++) p *= d;
        sum += p;
        temp /= 10;
    }
    return sum == n;
}
int main() { cout<<isArmstrong(153); return 0; }

