int main() { cout<<swapFirstLast(12345); return 0; }

solution:
#include <iostream>
using namespace std;
int swapFirstLast(int n) {
    int digits = 0, temp = n;
    while(temp > 0) { digits++; temp /= 10; }
    int first = n, mult = 1;
    while(first >= 10) { first /= 10; mult *= 10; }
    int last = n % 10;
    return n - first * mult - last + last * mult + first;
}
int main() { cout<<swapFirstLast(12345); return 0; }

