using namespace std;
bool isKaprekar(int n) {
    long sq = (long)n * n; int d = 0, t = n;
    while(t > 0) { d++; t /= 10; }
    int div = 1; for(int i=0;i<d;i++) div *= 10;
    return (sq%div + sq/div) == n && sq%div > 0;
}
