solution:
#include <iostream>
using namespace std;
bool isTech(int n) {
    int d = 0, t = n; while(t > 0) { d++; t /= 10; }
    if(d % 2 != 0) return false;
    int div = 1; for(int i=0;i<d/2;i++) div *= 10;
    int l = n/div, r = n%div;
    return (l+r)*(l+r) == n;
}
int main() { cout<<isTech(2025); return 0; }

