#include <iostream>
using namespace std;
struct Node { int v; Node* n; Node(int x):v(x),n(0){} };
Node* mergeTwo(Node* a, Node* b) { Node d(0),*t=&d; while(a&&b) { if(a->v<b->v) { t->n=a; a=a->n; } else { t->n=b; b=b->n; } t=t->n; } t->n=a?a:b; return d.n; }
Node* mergeKLists(Node* lists[], int k) { if(k==0) return 0; while(k>1) { int i=0,j=k-1; while(i<j) { lists[i]=mergeTwo(lists[i],lists[j]); i++; j--; } k=(k+1)/2; } return lists[0]; }
int main() { Node* l1=new Node(1); l1->n=new Node(4); l1->n->n=new Node(5); Node* l2=new Node(1); l2->n=new Node(3); l2->n->n=new Node(4); Node* l3=new Node(2); l3->n=new Node(6); Node* lists[]={l1,l2,l3}; Node* r=mergeKLists(lists,3); while(r) { cout<<r->v<<" "; r=r->n; } return 0; }

