using namespace std;
int cnt; bool col[20],d1[40],d2[40];
void solve(int r, int n) { if(r==n) { cnt++; return; } for(int c=0;c<n;c++) if(!col[c]&&!d1[r-c+n]&&!d2[r+c]) { col[c]=d1[r-c+n]=d2[r+c]=true; solve(r+1,n); col[c]=d1[r-c+n]=d2[r+c]=false; } }
int solveNQueens(int n) { cnt=0; solve(0,n); return cnt; }
