#include <iostream>
#include <string>
#include <vector>
using namespace std;

int josephus(int n, int k) {
    return 0;
}

int main() {
    int n;
    cin >> n;
    int k;
    cin >> k;
    cout << josephus(n, k);
    return 0;
}

solution:
#include <iostream>
using namespace std;
int josephus(int n, int k) { if(n==1) return 0; return (josephus(n-1,k)+k)%n; }
int main() { cout<<josephus(5,2); return 0; }

