solution:
#include <iostream>
using namespace std;
int gcd(int a, int b) { return b==0?a:gcd(b,a%b); }
bool canMeasure(int x, int y, int z) { return z<=x+y && z%gcd(x,y)==0; }
int main() { cout<<canMeasure(3,5,4); return 0; }

