using namespace std;

int bridgeCrossing(int a, int b, int c, int d) {
    return 0;
}

int main() {
    int a;
    cin >> a;
    int b;
    cin >> b;
    int c;
    cin >> c;
    int d;
    cin >> d;
    cout << bridgeCrossing(a, b, c, d);
    return 0;
}

solution:
#include <iostream>
using namespace std;
int bridgeCrossing(int a, int b, int c, int d) { int t[4]={a,b,c,d}; for(int i=0;i<4;i++) for(int j=i+1;j<4;j++) if(t[i]>t[j]) swap(t[i],t[j]); return min(2*t[1]+t[0]+t[3], t[0]+2*t[1]+t[2]+t[3]); }
int main() { cout<<bridgeCrossing(1,2,5,10); return 0; }

