solution:
#include <iostream>
#include <cmath>
using namespace std;
void roots(int a, int b, int c) { double d=b*b-4*a*c; double r1=(-b+sqrt(d))/(2*a), r2=(-b-sqrt(d))/(2*a); cout<<(int)r1<<" "<<(int)r2; }
int main() { roots(1,-5,6); return 0; }

