using namespace std;
void perm(char s[], int l, int r) { if(l==r) { cout<<s<<" "; return; } for(int i=l;i<=r;i++) { swap(s[l],s[i]); perm(s,l+1,r); swap(s[l],s[i]); } }
void permute(char s[]) { int n=0; while(s[n]) n++; perm(s,0,n-1); }
