solution:
#include <iostream>
using namespace std;
bool isPalin(char s[], int l, int r) { while(l<r) if(s[l++]!=s[r--]) return false; return true; }
int solve(char s[], int i, int n) { if(i>=n||isPalin(s,i,n-1)) return 0; int mn=1e9; for(int j=i;j<n-1;j++) if(isPalin(s,i,j)) mn=min(mn,1+solve(s,j+1,n)); return mn; }
int minCut(char s[]) { int n=0; while(s[n]) n++; return solve(s,0,n); }
int main() { cout<<minCut("aab"); return 0; }

